mapper.select:
select
    a.name as "name",
    a.data as "data",
    a.parent_id as "pId",
    a.id as "id"
from(
    select *
    from aweb_organization a
    where a.type = 'local_net'
    and a.lvl <= #{lvl}
    and (

        a.parent_ids like #{org.parentIds}

    )
    union
    select *
    from aweb_organization a
    where a.type = 'local_net'
    and a.lvl <= #{lvl}
    and id in (

        #{org.id}

    )
) a
order by a.lvl asc, a.sorting desc
