mapper.select:
select INDEX_CODE as "indexCode" , INDEX_NAME as "indexName" , to_char(BALANCE,'fm999,999,999,990.00') as "balance" , PRCTR as "prctr", PRCTR_NAME as "prctrName", SAP_FIN_CODE as "sapFinCode" from charge.RPT_BALANCE_DATA where report_month =#{month}
and PRCTR =#{prctrName}
and REPORT_ID = #{reportId}
order by report_id

select INDEX_CODE as "indexCode" , INDEX_NAME as "indexName" , to_char(BALANCE,'fm999,999,999,990.00') as "balance" , PRCTR as "prctr", PRCTR_NAME as "prctrName", SAP_FIN_CODE as "sapFinCode" from charge.RPT_BALANCE_DATA where report_month =#{month}
and PRCTR =#{prctrName}
and REPORT_ID = #{reportId}
order by report_id
