mapper.update:
update charge.dim_cut_cfg a set a.is_active = #{activeFlag}
where a.latn_id=#{latnId} and a.share_type=#{shareType} 
and a.income_source=#{incomeSource} and a.chg_who={userName}

mapper.select:
	select a.chg_who from charge.dim_cut_cfg a where
	 a.latn_id=#{latnId}
	 and a.share_type=#{shareType} 
	 and a.income_source=#{incomeSource} 
	 and a.chg_who=#{userName}
