select a.* from RPT_EXCEL_WYF a
join code_list_tax b on (b.type_code = 'income_source2018' and (b.code_id = #{incomeSource} or b.parent_code_id = #{incomeSource}) and a.incomesource_id = b.code_id)
where acct_month = #{month}
and a.area_name =#{latnId}

